Dialog.ClientEvent:
self.$show=function(config){
	if(!config.callback){
		dorado.MessageBox.alert("当前方法需要定义一个callback函数");
		return;
	}else{
		self.callback=config.callback;
	}
	if(config.caption){
		self.set("caption",config.caption);
	}
	if(config.width){
		self.set("width",config.width);
	}
	if(config.height){
		self.set("height",config.height);
	}
	if(config.selected){
		self.selected=config.selected;
	}
	self.show();
}

Button.ClientEvent:
var tree=view.id("$dataTreeDeptForSelect");
var nodes=tree.get("nodes");
var result=[];
buildNodes(tree.get("root"),result);
if(result.length==0){
	dorado.MessageBox.confirm("您没有选择部门，您真的要保存吗？",function(){
		view.id("$dialogDeptSelect").callback(result);
	});	
} else {
	view.id("$dialogDeptSelect").callback(result);
}
