}*/

function buildNodes(parentNode,resultArray){
	var nodes=parentNode.get("nodes");
	nodes.each(function(node){
		if(node.get("checked")){
			var data=node.get("data");
			resultArray.push(data.toJSON());		
		}
		buildNodes(node,resultArray);
	});	
}

DataTree.ClientEvent:
var dialog=view.id("$dialogDeptSelect");
var selected=dialog.selected;
if(selected){
	var data=arg.data;
	for(var i=0;i<selected.length;i++){
		if(data.get("id")==selected[i]){
			arg.node.set("checkable",false);
			break;
		}
	}
}
