DataPilot.ClientEvent:
var data=view.id("dataSetGroup").getData("#");
if(!data){
	dorado.MessageBox.alert("请先选中一个群组!");
	arg.processDefault=false;
	return;
}
var members = "users";
switch(arg.code){
	case "+":
		function initGroupCriterion(dataset, groupId){
			var sysParameter = dataset._sysParameter;
			if (!sysParameter) dataset._sysParameter = sysParameter = new dorado.util.Map();
			var criteria = sysParameter.get("criteria") || {};
			var criterions = criteria.criterions;
			if (!criterions){
				criteria.criterions = [{
					property:"groupId", 
					dataType:"String", 
					expression:groupId
				}];
			}else{
				var groupCriterion = null;
				for (var i = 0; i < criterions.length; i++) {
					var criterion = criterions[i];
					if (criterion.property=="groupId"){
						groupCriterion = criterion;
						break;
					}
				}
				if (groupCriterion==null){
					groupCriterion = {
						property:"groupId", 
						dataType:"String"
					}
					criterions.push(groupCriterion);
				}	
				groupCriterion.expression = groupId;
			}
			sysParameter.put("criteria", criteria);
		}
		var dialog=view.id("$dialogUserSelect");	
		var groupId = data.get("id");
		if (self.currentGroupId != groupId){
			self.currentGroupId = groupId;
			var dataSetUserForSelect = view.id("$dataSetUserForSelect");
			var dataSetUserSelected = view.id("$dataSetUserSelected");
			initGroupCriterion(dataSetUserForSelect, "!"+groupId);
			initGroupCriterion(dataSetUserSelected, groupId);
			dataSetUserForSelect.flushAsync();
			dataSetUserSelected.flushAsync();
		}
		dialog.$show({
			caption:"选择用户",
			width:700,
			callback:function(users){
				var ids=[];
				for(var i=0;i<users.length;i++){
					ids.push(users[i]["username"]);
				}			
				var action=view.id("ajaxActionInsertMember");
				action.set("parameter",{ids:ids,type:"user",groupId:data.get("id")});
				action.execute(function(error){
					if(error){
						for(var i=0;i<users.length;i++){
							var user=users[i];
							if(user["username"]==error){
								if(user["cname"]){
									error=user["cname"];
								}
								break;				
							}
						}
						dorado.MessageBox.alert("当前选择的["+error+"]已添加，请重新选择");
					}else{
						dialog.hide();
						data.reset("users");
					}
				});
			}
		});
		arg.processDefault=false;
		break;
	case "-":
		dorado.MessageBox.confirm("真的要删除当前成员吗？",function(){
			var action=view.id("ajaxActionDeleteMember");
			action.set("parameter",view.id("dataSetGroup").getData("#.#" + members+ ".id"));
			action.execute(function(){
				data.reset(members);
			});	
		});
		arg.processDefault=false;
		break;
}

var data=view.id("dataSetGroup").getData("#");
if(!data){
	dorado.MessageBox.alert("请先选中一个群组!");
	arg.processDefault=false;
	return;
}
var members = "depts";
switch(arg.code){
	case "+":
		var dialog=view.id("$dialogDeptSelect");	
		dialog.$show({
			caption:"选择部门",
			width:700,
			callback:function(deptArray){
				var ids=[];
					for(var i=0;i<deptArray.length;i++){
						ids.push(deptArray[i]["id"]);
					}		
				var action=view.id("ajaxActionInsertMember");
				action.set("parameter",{ids:ids,type:"dept",groupId:data.get("id")});
				action.execute(function(error){
					if(error){
							for(var i=0;i<deptArray.length;i++){
								if(error==deptArray[i]["id"]){
									error=deptArray[i]["name"];
									break;
								}
							}
							dorado.MessageBox.alert("部门["+error+"]已添加，请重新选择");
					}else{
						dialog.hide();
						data.reset(members);
					}
				});
			}
		});
		arg.processDefault=false;
		break;
	case "-":
		dorado.MessageBox.confirm("真的要删除当前成员吗？",function(){
			var action=view.id("ajaxActionDeleteMember");
			action.set("parameter",view.id("dataSetGroup").getData("#.#" + members+ ".id"));
			action.execute(function(){
				data.reset(members);
			});	
		});
		arg.processDefault=false;
		break;
}

