var members = "positions";
switch(arg.code){
	case "+":
		var dialog=view.id("$dialogPositionSelect");	
		dialog.$show({
			caption:"选择岗位",
			width:700,
			callback:function(chosenArray){
				var ids=[];
					for(var i=0;i<chosenArray.length;i++){
						ids.push(chosenArray[i]["id"]);
					}		
				var action=view.id("ajaxActionInsertMember");
				action.set("parameter",{ids:ids,type:"position",groupId:data.get("id")});
				action.execute(function(error){
					if(error){
							for(var i=0;i<chosenArray.length;i++){
								if(error==chosenArray[i]["id"]){
									error=chosenArray[i]["name"];
									break;
								}
							}
							dorado.MessageBox.alert("岗位["+error+"]已添加，请重新选择");
					}else{
						dialog.hide();
						data.reset(members);
					}
				});
			}
		});
		arg.processDefault=false;
		break;
	case "-":
		dorado.MessageBox.confirm("真的要删除当前成员吗？",function(){
			var action=view.id("ajaxActionDeleteMember");
			action.set("parameter",view.id("dataSetGroup").getData("#.#" + members+ ".id"));
			action.execute(function(){
				data.reset(members);
			});	
		});
		arg.processDefault=false;
		break;
}

var ds = view.id("dataSetGroup");
switch(arg.code) {
	case '+':
	    ds.insert();
		view.id("dialogGroup").show();
		arg.processDefault = false;
		break;
	case '-':
		dorado.MessageBox.confirm("确认要删除当前数据?", function(){
			ds.getData("#").remove();
			view.id("updateActionGroup").execute();
		});
		arg.processDefault = false;
		break;
}
