View.ClientEvent:
this.$checkSaveButton = function(){
    var toolBarButtonSave = this.id("toolBarButtonSave");
    var dataSet = this.id("dataSetData");
    var data = dataSet.queryData("[#dirty]");
    if (data!="") {
        toolBarButtonSave.set("disabled", false);
    }
    else {
        toolBarButtonSave.set("disabled", true);
    }
    
}

DataGrid.ClientEvent:
var data = arg.data;
var column = arg.column;
arg.processDefault = false;
if (column instanceof dorado.widget.grid.RowNumColumn) {
    arg.processDefault = true;
}else {
    var value = data.get(column.get("name"));
    var re = new RegExp(/^<font color=/);
    if (re.test(value)) {
        arg.dom.innerHTML = value;
    }    else {
        arg.processDefault = true;
    }}
